a<template>
    <div class="container">
        <div class="row">
            <div class="col">
                <h1 class="mt-3">Book</h1>
            </div>
        </div>
    </div>
</template>


<script>
// eslint-disable-next-line
export default {
  name: 'App'
  data() {
    return {
      book: {},
      imgPath: process.env.VUE_APP_IMAGE_URL,
      ready: false,
    }
  },
  activated() {
    fetch(process.env.VUE_APP_API_URL + "/books/" + this.$route.params.bookName)
        .then((response) => response.json())
        .then((data) => {
          if (data.error) {
            this.$emit('error', data.message)
          } else {
            this.book = data.data;
            this.ready = true;
            console.log("Title is", this.book.title);
          }
        })
  },
  deactivated() {

  }
}
</script>
